__ht__function(g, rect, comp, data, view) {
// 컴포넌트 x,y, 너비,높이값
var x = rect.x
var y = rect.y
var rectWidth = rect.width;
var rectHeight = rect.height;
var waveForeground = addCreateLinear(comp.getValue('colorArr'));
var waveBackground = comp.getValue('background');
var startX = 0;
var waveWidth = comp.getValue('waveWidth');
var waveHeight = comp.getValue('waveHeight');
var wareXOffset = comp.getValue('wareXOffset');
var percentage = comp.getValue('percentage');

var points = [];

// 创建渐变背景色
function addCreateLinear(colorsArr) {
    var linear = g.createLinearGradient(0, 0, 0, rectHeight);
    var len = colorsArr.length;
    colorsArr.forEach(function(item, index) {
        linear.addColorStop((index + 1) / len, item);
    });
    return linear;
}

// 绘制波浪函数
function drawWave(xOffset, fillStyle, direction) {
    g.beginPath();
    for (var x = startX; x < startX + rectWidth; x += 20 / rectWidth) {
        var y =
            waveHeight *
            Math.sin((startX + x) * waveWidth + xOffset * direction);
        points.push([x, rectHeight * (1 - percentage) + y]);
        g.lineTo(x, rectHeight * (1 - percentage) + y);
    }
    g.lineTo(rectWidth, rectHeight);
    g.lineTo(startX, rectHeight);
    g.lineTo(points[0][0], points[0][1]);
    g.fillStyle = fillStyle;
    g.fill();
}

g.save();

g.beginPath();
g.rect(x, y, rectWidth, rectHeight);
g.clip();

if (percentage === 1) {
    g.beginPath();
    g.fillStyle = waveForeground;
    g.fillRect(x, y, rectWidth, rectHeight);
} else {
    drawWave(wareXOffset + 0.75, waveBackground, 1);
    drawWave(wareXOffset, waveForeground, 1);
}

// restore
g.restore();
}
