renderHTML:
__ht__function(data, gv, cache) {var option = {
    tooltip : {
        trigger: 'axis',
        formatter: '{b}월</br>월간 전력 사용량:{c}',
        axisPointer : {
            type : 'shadow'
        }
    },
    grid: {
        left: 0,
        right: 0,
        bottom: 0,
        top: '5%',
        containLabel: true
    },
    xAxis : [
        {
            type : 'category',
            axisLine: {
                lineStyle: {
                    color: '#ffffff'
                }
            },
            axisTick: {
                alignWithLabel: true
            },
            axisLabel: {
                interval: 0,
                color: '#ffffff'
            }
        }
    ],
    yAxis : [
        {
            type : 'value',
            min: 0,
            max: 10,
            axisLine: {
                show: false
            },
            axisTick: {
                show: false,
                alignWithLabel: true
            },
            axisLabel: {
                interval: 0,
                color: '#ffffff'
            }
        }
    ],
    series : [
        {
            name:'월간전력량',
            type:'bar',
            barWidth: '60%',
            itemStyle: {
                color:  new echarts.graphic.LinearGradient(
                    0, 0, 0, 1,
                    [
                        {offset: 0, color: '#ffff43'},
                        {offset: 1, color: '#ffba00'}
                    ]
                ),
                barBorderRadius: [2, 2, 0, 0]
            }
        }
    ]
};

if (!cache.htmlView) {
    cache.htmlView = document.createElement('div');
    cache.htmlView.style.position = 'absolute';
    cache.htmlChart = echarts.init(cache.htmlView);

    cache.htmlView.layoutHTML = function() {
        gv.layoutHTML(data, cache.htmlView, false);
        cache.htmlChart.resize();
    };
}

// update data
option.xAxis[0].data = data.a('xAxisData');
option.series[0].data = data.a('seriesData');
cache.htmlChart.setOption(option);

// html for showing
return cache.htmlView;}
