renderHTML:
__ht__function(data, gv, cache) {var option = {
        tooltip : {
            trigger: 'item',
            formatter: "{b}: {c} ({d}%)"
        },
        legend: {
            orient: 'vertical',
            itemHeight:10,
            itemWidth:10,
            x: 'right',
            align:'left',
            right: '5%',
            data:[],
            top: "top",
            textStyle: {
            color: '#f2f2f2',
            fontSize: 25,

         },
         icon: 'roundRect',
        },
        series : [
            {
                name: '',
                type:'pie',
                center: ['40%', '50%'],
                radius: ['45%', '80%'],
                avoidLabelOverlap: false,
                label: {
                    normal: {
                        show: false,
                        position: 'center'
                    },
                    emphasis: {
                        show: true,
                        textStyle: {
                            fontSize: '34',
                            fontWeight: 'bold'
                        }
                    }
                },
                labelLine: {
                    normal: {
                        show: false
                    }
                },
                data:[
                        {value:335, name:'直接访问'},
                    ]
            },
        ]
    };

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }
    // update data
    option.color = data.a('pieColor');
    option.legend.data = data.a('seriesData');
    option.series[0].data = data.a('seriesData');
    option.series[0].data.value = data.a('seriesData.value');
    option.textStyle = { color: '#fff' };
    option.legend.textStyle = { color: data.a('pieColor') };
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;}
