renderHTML:
__ht__function(data, gv, cache) {var option = {
        tooltip : {
        trigger: 'item',
        formatter: "{b} : {c} ({d}%)"
    },

    visualMap: {
        show: false,
        min: 40,
        max: 600,
        inRange: {
            colorLightness: [0, 1]
        }
    },
    series : [
        {
            name:'',
            type:'pie',
            radius : ['45%','85%'],
            center: ['50%', '50%'],
            data:[ ].sort(function (a, b) { return b.value - a.value; }),
            roseType: 'radius',
            label: {
                normal: {
                    textStyle: {
                        color: 'rgba(255, 255, 255, 0.3)'
                    }
                }
            },
            labelLine: {
                normal: {
                    lineStyle: {
                        color: 'rgba(255, 255, 255, 0.3)'
                    },
                    smooth: 0.2,
                    length: 5,
                    length2: 10
                }
            },
            itemStyle: {
                normal: {
                    color: ['#8e44ad'],
                     shadowBlur: 20,
                    shadowColor: 'rgba(0, 0, 0, 0.2)'

                }
            },
            animationType: 'scale',
            animationEasing: 'elasticOut',
            animationDelay: function (idx) {
                return Math.random() * 200;
            }
        }
    ]
    };

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }
    // update data
    option.series[0].itemStyle.normal.color = data.a('pieColor');
    option.series[0].data = data.a('seriesData');
    option.visualMap.inRange.colorLightness = data.a('lightnessArray');
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;}
