renderHTML:
__ht__function(data, gv, cache) {if (data.__update__) return;

    var dayMap = {
        1: '월요일',
        2: '화요일',
        3: '수요일',
        4: '목요일',
        5: '금요일',
        6: '토요일',
        7: '일요일'
    };

    var addZero = function(v) {
        if (v < 10) return '0' + v;
        return v;
    };

    var update = data.__update__ = function() {
        var date = new Date();
        this.a('time', addZero(date.getHours()) + ':' + addZero(date.getMinutes()));
        this.a('week', dayMap[date.getDay()]);
        this.a('date', date.getFullYear() + '.' + addZero(date.getMonth() + 1) + '.' + addZero(date.getDate()));
        setTimeout(function () {
            update();
        }, 1000 * 3);
    }.bind(data);

    update();}
