renderHTML:
__ht__function(data, gv, cache) {var option = {
    title: {
        text: 'Allocated',
        textStyle: {
            color: '#fff'
        }
    },
    tooltip: {},
    legend: {
        show:false,
        data: ['Allocated Budget', 'Actual Spending'],
        textStyle: {
            color: '#fff'
        },
        orient: 'vertical',
        right: 'right',
        selectedMode: 'single',
        itemGap: 20,
        itemWidth: 10,
        itemHeight:10
    },
    radar: {
        // shape: 'circle',
        name: {
            textStyle: {
                color: '#fff',
                backgroundColor: '#999',
                borderRadius: 3,
                padding: [2, 2],
                fontSize: 12
           }
        },
        radius:"55%",
        indicator:
        [
           { name: '（sales）', max: 6500},
           { name: '（Administration）', max: 16000},
           { name: '（Information Techology）', max: 30000},
           { name: '（Customer Support）', max: 38000},
           { name: '（Development）', max: 52000},
           { name: '（Marketing）', max: 25000}
        ],
        splitNumber: 4,
        shape: 'circle', //polygon
        name: {
            formatter:'{value}',
            textStyle: {
                color:'#eee'
            }
        },
        splitArea: {
            areaStyle: {
                color: ['rgba(114, 172, 209, 0.2)',
                'rgba(114, 172, 209, 0.4)', 'rgba(114, 172, 209, 0.6)',
                'rgba(114, 172, 209, 0.8)', 'rgba(114, 172, 209, 1)'],
                shadowColor: 'rgba(0, 0, 0, 0.3)',
                shadowBlur: 10
            }
        },
        axisLine: {
            lineStyle: {
                color: 'rgba(255, 255, 255, 0.5)'
            }
        },
        splitLine: {
            lineStyle: {
                color: 'rgba(255, 255, 255, 0.5)'
            }
        }
    },
    series: [{
        name: '（Budget vs spending）',
        type: 'radar',
        // areaStyle: {normal: {}},
        data : [
            {
                value : [4300, 10000, 28000, 35000, 50000, 19000],
                name : 'Allocated Budget',
                symbol: 'circle',
                symbolSize: 5,
                lineStyle: {
                    normal: {
                        type: 'dashed', //'solid''dashed''dotted'
                        color: 'rgba(255,255,255,1)'
                    }
                },
                itemStyle: {
                normal: {
                    color: '#B3E4A1'
                    }
                },
                areaStyle: {
                    normal: {
                        color: 'rgba(255, 255, 255, 0.5)'
                    }
                }
            },
             {
                value : [5000, 14000, 28000, 31000, 42000, 21000],
                name : 'Actual Spending',
                symbol: 'circle',
                symbolSize: 5,
                lineStyle: {
                    normal: {
                        type: 'solid', //'solid''dashed''dotted'
                        color: ''
                    }
                },
                itemStyle: {
                normal: {
                    color: '#B3E4A1'
                    }
                },
                areaStyle: {
                    normal: {
                        color: 'rgba(255, 255, 255, 0.5)'
                    }
                }
            }
        ]
    }]
};

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // update data
    option.title.text = data.a('chartTitle');
    option.legend.data = [data.a('legendData1'), data.a('legendData2') ];
    option.legend.selectedMode = data.a('selectMode');
    option.series[0].name = data.a('seriesName');
    // option.series[0].data = [data.a('lineData1'), data.a('lineData2')];
    option.series[0].data[0].name = data.a('legendData1');
    option.series[0].data[1].name = data.a('legendData2');
    option.series[0].data[0].value = data.a('lineData1');
    option.series[0].data[1].value = data.a('lineData2');

    //line style
    option.series[0].data[0].lineStyle.normal.type = data.a('lineDataType1');
    option.series[0].data[1].lineStyle.normal.type = data.a('lineDataType2');
    option.series[0].data[0].lineStyle.normal.color = data.a('lineDataColor1');
    option.series[0].data[1].lineStyle.normal.color = data.a('lineDataColor2');

    //area style
    option.series[0].data[0].areaStyle.normal.color = data.a('areaColor1');
    option.series[0].data[1].areaStyle.normal.color = data.a('areaColor2');

    //item style
    option.series[0].data[0].itemStyle.normal.color = data.a('lineDataColor1');
    option.series[0].data[1].itemStyle.normal.color = data.a('lineDataColor2');  

    //indicator
    option.radar.splitArea.areaStyle.color = data.a('areaArrayColor');
    option.radar.indicator = data.a('indicator');
    option.radar.shape = data.a('shape');

    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;}
