renderHTML:
__ht__function(data, gv, cache) {var myColor = ['#F44336', '#FF9800', '#d0570e'];
option = {
    backgroundColor:'',
        tooltip: {
          trigger: 'axis',
          axisPointer: { // 坐标轴指示器，坐标轴触发有效
            type: 'shadow' // 默认为直线，可选为：'line' | 'shadow'
          }
    },
    grid: {
          left: '4%',
          right: '5%',
          bottom: '5%',
          top:'28%',
          containLabel: true
    },
    legend: {
        data: ['A', 'B', 'C'],
        right: 10,
        top:12,
        textStyle: {
            color: "#fff"
        },
        itemWidth: 12,
        itemHeight: 10,
        // itemGap: 35
    },
    xAxis: {
          type: 'category',
          data: ['2016','2017','2018','2019'],
          axisLine: {
            lineStyle: {
              color: 'rgba(149, 165, 166,0.5)'

            }
          },
          axisLabel: {
            // interval: 0,
            // rotate: 40,
            textStyle: {
              fontFamily: 'Microsoft YaHei'
            }
          },
    },
    yAxis: {
          type: 'value',
          axisLine: {
            show: false,
            lineStyle: {
              color: 'rgba(149, 165, 166,0.5)'
            }
          },
          splitLine: {
            show: true,
            lineStyle: {
              color: 'rgba(149, 165, 166,0.5)'
            }
          },
          axisLabel: {}
    },
        series: [{
          name: 'A',
          type: 'bar',
          barWidth: '15%',
          itemStyle: {
            normal: {
                color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                    offset: 0,
                    color: '#fccb05'
                }, {
                    offset: 1,
                    color: '#f5804d'
                }]),
                barBorderRadius: 12,
            },
          },
          data: [400, 400, 300, 300]
        },
        {
          name: 'B',
          type: 'bar',
          barWidth: '15%',
          itemStyle: {
            normal: {
                color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                    offset: 0,
                    color: '#8bd46e'
                }, {
                    offset: 1,
                    color: '#09bcb7'
                }]),
                barBorderRadius: 11,
            }

          },
          data: [400, 500, 500, 500]
        },
        {
          name: 'C',
          type: 'bar',
          barWidth: '15%',
          itemStyle: {
            normal: {
                color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                    offset: 0,
                    color: '#248ff7'
                }, {
                    offset: 1,
                    color: '#6851f1'
                }]),
            barBorderRadius: 11,
            }
          },
          data: [400, 600, 700, 700]
        }]
};
    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // // dispatch action
        var app = {
        currentIndex: -1,
      };  
      setInterval(function () {
        var dataLen = option.series[0].data.length;

        cache.htmlChart.dispatchAction({
          type: 'downplay',
          seriesIndex: 0,
          dataIndex: app.currentIndex
        });
        app.currentIndex = (app.currentIndex + 1) % dataLen;
        //console.log(app.currentIndex);
        cache.htmlChart.dispatchAction({
          type: 'highlight',
          seriesIndex: 0,
          dataIndex: app.currentIndex,
        });
        cache.htmlChart.dispatchAction({
          type: 'showTip',
          seriesIndex: 0,
          dataIndex: app.currentIndex
        });
      }, 8000);

    // update data
    option.series[0].data = data.a('seriesDataA');
    option.series[1].data = data.a('seriesDataB');
    option.series[2].data = data.a('seriesDataC');
    option.series[0].name = data.a('seriesName')[0];
    option.series[1].name = data.a('seriesName')[1];
    option.series[2].name = data.a('seriesName')[2];    
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;}
