renderHTML:
__ht__function(data, gv, cache) {var fontColor = '#30eee9';
var option = {
		grid: {
	        left: '0%',
            right: '5%',
            top:'15%',
	        bottom: '5%',
	        containLabel: true
		},
		tooltip : {
			show: true,
			trigger: 'item'
		},
		legend: {
			show:true,
			x:'right',
			y:'0',
			icon: 'stack',
			itemWidth:10,
			itemHeight:10,
			textStyle:{
				color:'#1bb4f6'
			},
			data:['已采纳','已发布']
		},
		xAxis : [
	        {
	            type : 'category',
	            boundaryGap : false,
	            axisLabel:{
	            	color: fontColor
	            },
	            axisLine:{
               		show:true,
               		lineStyle:{
		            	color:'#397cbc'
		            }
				},
				axisTick:{
	            	show:false,
	            },  
	            splitLine:{
	            	show:false,
		            lineStyle:{
		            	color:'#195384'
		            }
		        },
	            data : ['1','2','3','4','5','6','7']
	        }
	    ],
	    yAxis : [
			{
				type : 'value',
				axisLabel : {
					formatter: '{value}',
					textStyle:{
						color:'#2ad1d2'
					}
				},
				axisLine:{
                    show:false,
					lineStyle:{
						color:'#27b4c2'
					}
				},
				axisTick:{
	            	show:false,
	            },
				splitLine:{
					show:false,
					lineStyle:{
						color:'#11366e'
					}
				}
			}
		],
		series : [
			{
				name:'已采纳',
				type:'line',
				stack: '总量',
				symbol:'circle',
				symbolSize: 8,
	            itemStyle: {
			        normal: {
						color:'#0092f6',
			            lineStyle: {
							color: "#0092f6",
							width:1
			            },
			            areaStyle: { 
							//color: '#94C9EC'
							color: new echarts.graphic.LinearGradient(0, 1, 0, 0, [{
								offset: 0,
								color: 'rgba(7,44,90,0.3)'
							}, {
								offset: 1,
								color: 'rgba(0,146,246,0.9)'
							}]),
			            }
			        }
				},
				markPoint:{
					itemStyle:{
						normal:{
							color:'red'
						}
					}
				},
				data:[120, 132, 101, 134, 90, 230, 210, 182, 191, 234, 290, 330]
			},
			{
				name:'已发布',
				type:'line',
				stack: '总量',
				symbol:'square',
				symbolSize: 8,

	            itemStyle: {
			        normal: {
			            color:'#00d4c7',
			            lineStyle: {
							color: "#00d4c7",
							width:1
			            },
			            areaStyle: { 
							//color: '#94C9EC'
							color: new echarts.graphic.LinearGradient(0, 1, 0, 0, [{
								offset: 0,
								color: 'rgba(7,44,90,0.3)'
							}, {
								offset: 1,
								color: 'rgba(0,212,199,0.9)'
							}]),
			            }
			        }
				},
				data:[220, 182, 191, 234, 290, 330, 310,201, 154, 190, 330, 410]
			}
		]
    };

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // update data
    option.legend.data = [data.a('legendData1'), data.a('legendData2') ];
    option.series[0].name = data.a('legendData1');
    option.series[1].name = data.a('legendData2');
    option.series[0].data = data.a('lineData1');
    option.series[1].data = data.a('lineData2');
    option.legend.textStyle.color = data.a('textColor');
    option.xAxis[0].data = data.a('xAxisData');
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;}
