renderHTML:
__ht__function(data, gv, cache) {var option = {
    title: {
        text: "84%",
        subtext: '전력 사용량',
        x: 'center',
        y: 'center',
        textStyle: {
            color: "#fff",
            fontSize: '160%',
            fontWeight: 'normal'
        },
        subtextStyle: {
            color: "rgba(255,255,255,.8)",
            fontSize: 14,
            fontWeight: 'normal'
        }
    },
    tooltip: {
        trigger: 'item',
        formatter: "{a} <br/>{b} : {c} ({d}%)"
    },
    legend: {
        x: 'center',
        y: 'bottom',
        data: ['rose3', 'rose5', 'rose6', 'rose7', 'rose8']
    },
    calculable: true,
    series: [
        //굵은 차트
        {
            name: '토지 면적',
            type: 'pie',
            radius: ['50%','66%'],
            center: ['50%', '50%'],
            data: [{
                    value: 48,
                    name: '건물 면적',
                    itemStyle: {
                        color: new echarts.graphic.LinearGradient(0, 1, 0, 0, [{
                            offset: 0,
                            color: '#f6e3a1'
                        }, {
                            offset: 1,
                            color: '#ff4236'
                        }])
                    },
                    label: { 
                        fontSize: 14,
                        color: "rgba(255,255,255,.7)",
                        formatter: `건물 면적 \n {c} ㎡`,
                        rich: {
                            a: {
                                color: "#fff",
                                fontSize: 16,
                                lineHeight: 10
                            },
                        }
                    }
                },
                {
                    value: 52,
                    name: '부지 면적',
                    itemStyle: {
                        color: "transparent"
                    }
                }
            ]
        },

        // 얇은 차트
        {
            name: '전기용량',
            type: 'pie',
            radius: ['56%','60%'],
            center: ['50%', '50%'],
            data: [{
                    value: 48,
                    name: '건물 면적',
                    itemStyle: {
                        color: "transparent"
                    }
                },
                {
                    value: 52,
                    name: '부지 면적',
                    itemStyle: {
                        color: new echarts.graphic.LinearGradient(0, 1, 0, 0, [{
                            offset: 0,
                            color: '#348fe6'
                        }, {
                            offset: 1,
                            color: '#625bef'
                        }])
                    },
                    label: {
                        fontSize: 14,
                        color: "rgba(255,255,255,.7)",
                        formatter: `부지 면적 \n {c} ㎡`,
                        rich: {
                            a: {
                                color: "#fff",
                                fontSize: 16,
                                lineHeight: 10
                            },
                        }
                    },
                }
            ]
        }
    ]
};

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }
    // update data
    usageValue = data.a('seriesData1');
    storageValue = data.a('seriesData2');
    option.series[0].data[0].value = data.a('seriesData1');
    option.series[0].data[1].value = data.a('seriesData2');
    option.series[1].data[0].value = data.a('seriesData1');
    option.series[1].data[1].value = data.a('seriesData2');
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;}
