renderHTML:
__ht__function(data, gv, cache) {option = {
    tooltip : {
        trigger: 'item',
        formatter: "{b} : {c}"
    },
    grid: {
            left: '3%',
            right: '3%',
            bottom:'5%',
            top: '10%',
            containLabel: true
        },
    xAxis: {
        data: [
            "a",
            "b",
            "c",
            "d",
            "e",
            "f",
            "g",        
        ],
        axisLine: {
            show: false //隐藏X轴轴线
        },
        axisTick: {
            show: false //隐藏X轴刻度
        },
        axisLabel: {
            show: false,
            textStyle: {
                color: "#ebf8ac" //X轴文字颜色
            }
        },
         axisLine: {
            show: false,
            lineStyle: {
                color: '#FFFFFF'
            }
        },
    },
    yAxis: [{
            type: "value",
            name: "Y",
            nameTextStyle: {
                color: "#ebf8ac"
            },
            splitLine: {
                show: false
            },
            splitLine: {
                show: false
            },
            axisTick: {
                show: false
            },
            axisLine: {
                show: false
            },
            axisLabel: {
                show: false,
                textStyle: {
                    color: "#ebf8ac"
                }
            },
             axisLine: {
                show: false,
                lineStyle: {
                    color: '#FFFFFF'
                }
            },
        },
        {
            type: "value",
            gridIndex: 0,
            min: 50,
            max: 100,
            splitNumber: 8,
            splitLine: {
                show: false
            },
            axisLine: {
                show: false
            },
            axisTick: {
                show: false
            },
            axisLabel: {
                show: false
            },
        }
    ],
    series : [{
            type: 'bar',
            yAxisIndex: 0,
            data: [4.2, 3.8, 4.8, 3.5, 2.9, 2.8, 3],
            barWidth: 15,
            itemStyle: {
                normal: {
                    color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                            offset: 0,
                            color: "#00FFE3"
                        },
                        {
                            offset: 1,
                            color: "#4693EC"
                        }
                    ])
                }
            }
        }]
};

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // update data
    option.series[0].data = data.a('seriesData');
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;}
