renderHTML:
__ht__function(data, gv, cache) {var option = {
        tooltip : {
            trigger: 'axis',
            axisPointer: {
                type: 'line',
                label: {
                    backgroundColor: '#6a7985'
                }
            }
        },
        grid: {
            left: '3%',
            right: '5%',
            bottom: 0,
            top: '5%',
            containLabel: true
        },
        xAxis : [
            {
                type : 'category',
                boundaryGap : false,
                axisLine: {show: false},
                splitLine: {show: false},
                axisLabel: {}
            }
        ],
        yAxis : [
            {
                type : 'value',
                axisLine: {show: false},
                splitLine: {show: false},
                axisLabel: {}
            }
        ],
        series : [
            {
                type:'line',
                areaStyle: {
                    normal: {
                        color: 'rgba(27,85,245,0.40)',
                        opacity: 0.4
                    }
                }
            },
            {
                type:'line',
                areaStyle: {
                    normal: {
                        color: 'rgba(252,209,134,0.40)',
                        opacity: 0.4
                    }
                }
            },
        ]
    };

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // update data
    option.color = [data.a('lineColor1')];
    option.series[0].name = data.a('legendData1');
    option.series[0].data = data.a('lineData1');
    option.series[0].areaStyle.normal.color = data.a('lineColor1');
    option.series[0].smooth = data.a('lineSmooth');
    option.textStyle = { color: data.a('textColor') };
    option.xAxis[0].data = data.a('xAxisData');
    option.yAxis[0].min = data.a('yAxisMin');
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;}
