option.series[5].name = data.a('legendData6');
option.series[5].data = data.a('lineData6');
option.series[5].areaStyle.normal.color = data.a('lineColor6');
option.textStyle = { color: data.a('textColor') };
option.xAxis[0].data = data.a('xAxisData');
cache.htmlChart.setOption(option);

// html for showing
return cache.htmlView;}
